import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';

const CarList = ({ url }) => {
  const [list, setList] = useState([]);

  // Fetch list of cars
  const fetchList = useCallback(async () => {
    try {
      const response = await axios.get(`${url}/api/cars`);
      setList(response.data);
    } catch (error) {
      console.error('Error fetching list:', error);
      toast.error('Error fetching cars');
    }
  }, [url]);

  // Delete a car
  const removeCar = async (carId) => {
    try {
      await axios.delete(`${url}/api/cars/${carId}`);
      toast.success('Car deleted successfully');
      fetchList(); // Refresh list
    } catch (error) {
      console.error('Error deleting car:', error);
      toast.error('Failed to delete car');
    }
  };

  useEffect(() => {
    fetchList();
  }, [fetchList]);

  return (
    <div className="flex flex-col p-4 space-y-4">
      <h2 className="text-xl font-bold">All Cars List</h2>

      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200 border">
          <thead className="bg-gray-100">
            <tr>
              <th className="px-4 py-2 text-left text-sm font-medium text-gray-700">Image</th>
              <th className="px-4 py-2 text-left text-sm font-medium text-gray-700">Name</th>
              <th className="px-4 py-2 text-left text-sm font-medium text-gray-700">Model</th>
              <th className="px-4 py-2 text-left text-sm font-medium text-gray-700">Year</th>
              <th className="px-4 py-2 text-left text-sm font-medium text-gray-700">Price</th>
              <th className="px-4 py-2 text-left text-sm font-medium text-gray-700">Action</th>
            </tr>
          </thead>

          <tbody className="bg-white divide-y divide-gray-200">
            {list.map((item) => (
              <tr key={item._id} className="hover:bg-gray-50">
                <td className="px-4 py-2">
                  <img
                    src={`${url}/images/${item.images[0]}`}
                    alt={item.name}
                    className="w-20 h-16 object-cover rounded"
                  />
                </td>
                <td className="px-4 py-2 text-sm text-gray-700">{item.name}</td>
                <td className="px-4 py-2 text-sm text-gray-700">{item.model}</td>
                <td className="px-4 py-2 text-sm text-gray-700">{item.year}</td>
                <td className="px-4 py-2 text-sm text-gray-700">${item.price}</td>
                <td className="px-4 py-2">
                  <button
                    onClick={() => removeCar(item._id)}
                    className="text-red-500 hover:text-red-700 font-bold"
                  >
                    X
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default CarList;
